changeSet.sql:
CREATE TABLE public.roles(
  id SERIAL UNIQUE NOT NULL,
  name VARCHAR(20)
);

CREATE TABLE public.users(
  id SERIAL UNIQUE NOT NULL,
  login VARCHAR(20) NOT NULL,
  password VARCHAR(30) NOT NULL,
  roles_id INTEGER REFERENCES roles(id),
  PRIMARY KEY (id)
);

